import{_ as e,c as a,o as t,a as o}from"./app.42516e9a.js";const w=JSON.parse('{"title":"Gate 1 Kakul-Saydon","description":"","frontmatter":{"title":"Gate 1 Kakul-Saydon"},"headers":[{"level":2,"title":"Preparation","slug":"preparation"},{"level":2,"title":"130 HP Stagger check","slug":"_130-hp-stagger-check"},{"level":2,"title":"110 HP Love or Hate","slug":"_110-hp-love-or-hate"},{"level":2,"title":"85 HP Simon Says","slug":"_85-hp-simon-says"},{"level":2,"title":"65 HP - Stagger check","slug":"_65-hp-stagger-check"},{"level":2,"title":"45 HP Roulette","slug":"_45-hp-roulette"},{"level":2,"title":"25 HP Love or Hate","slug":"_25-hp-love-or-hate"},{"level":2,"title":"\u221E HP Dice","slug":"\u221E-hp-dice"}],"relativePath":"kakul-saydon/index.md","lastUpdated":1678359278000}'),r={name:"kakul-saydon/index.md"},i=o('<h1 id="gate-1-kakul-saydon-normal-mode" tabindex="-1">Gate 1 Kakul-Saydon <code>--Normal Mode--</code> <a class="header-anchor" href="#gate-1-kakul-saydon-normal-mode" aria-hidden="true">#</a></h1><p>Table of content</p><nav class="table-of-contents"><ul><li><a href="#preparation">Preparation</a><ul></ul></li><li><a href="#_130-hp-stagger-check">130 HP Stagger check</a></li><li><a href="#_110-hp-love-or-hate">110 HP Love or Hate</a></li><li><a href="#_85-hp-simon-says">85 HP Simon Says</a></li><li><a href="#_65-hp-stagger-check">65 HP - Stagger check</a></li><li><a href="#_45-hp-roulette">45 HP Roulette</a></li><li><a href="#_25-hp-love-or-hate">25 HP Love or Hate</a></li><li><a href="#\u221E-hp-dice">\u221E HP Dice</a><ul></ul></li></ul></nav><h2 id="preparation" tabindex="-1">Preparation <a class="header-anchor" href="#preparation" aria-hidden="true">#</a></h2><h4 id="items" tabindex="-1">Items: <a class="header-anchor" href="#items" aria-hidden="true">#</a></h4><p>HP potion</p><p>stagger grenade</p><h4 id="ester" tabindex="-1">Ester <a class="header-anchor" href="#ester" aria-hidden="true">#</a></h4><p>Agile</p><h4 id="positions" tabindex="-1">Positions <a class="header-anchor" href="#positions" aria-hidden="true">#</a></h4><p>1/5/7/11 o&#39;clock 1 per party</p><h4 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-hidden="true">#</a></h4><p>Turning into a clown is trolling</p><h2 id="_130-hp-stagger-check" tabindex="-1">130 HP <strong>Stagger check</strong> <a class="header-anchor" href="#_130-hp-stagger-check" aria-hidden="true">#</a></h2><p>Positional stagger check, don;t stay facing pink shield</p><p>This mechanic will repeat at 65HP</p><h2 id="_110-hp-love-or-hate" tabindex="-1">110 HP <strong>Love or Hate</strong> <a class="header-anchor" href="#_110-hp-love-or-hate" aria-hidden="true">#</a></h2><p>4 clowns will appear at the corners of a map, each clown will show either <code>heart</code> or <code>guns with confetti</code>. You must look at the clown who is in minority, either <code>guns</code> or <code>heart</code></p><p>Go to your assigned position and ping near a clown if it shows you a <code>heart</code></p><p>If 3 clowns have a <code>heart</code> look at the clown with <code>confetti guns</code></p><p>If 3 clown have a <code>confetti guns</code> look at the clown with a <code>heart</code></p><h2 id="_85-hp-simon-says" tabindex="-1">85 HP <strong>Simon Says</strong> <a class="header-anchor" href="#_85-hp-simon-says" aria-hidden="true">#</a></h2><p>Stand in area that has your suit</p><p>Clown will do an emote you must repeat same emote if he is facing you</p><p>Use any other emote if the clown turn away</p><h2 id="_65-hp-stagger-check" tabindex="-1">65 HP - <strong>Stagger check</strong> <a class="header-anchor" href="#_65-hp-stagger-check" aria-hidden="true">#</a></h2><p>Positional stagger check, don;t stay facing pink shield Same as 65HP bar</p><h2 id="_45-hp-roulette" tabindex="-1">45 HP <strong>Roulette</strong> <a class="header-anchor" href="#_45-hp-roulette" aria-hidden="true">#</a></h2><p>Huge roulette will appear you must stay in pizza slice matching your suit and background</p><p>Roulette will spin 3 times and will explode after.</p><h2 id="_25-hp-love-or-hate" tabindex="-1">25 HP <strong>Love or Hate</strong> <a class="header-anchor" href="#_25-hp-love-or-hate" aria-hidden="true">#</a></h2><p>Same mechanic as <a href="#_110-hp-love-or-hate">110 bars</a></p><p>4 clowns will appear at the corners of a map, each clown will show either <code>heart</code> or <code>guns with confetti</code>. You must look at the clown who is in minority, either <code>guns</code> or <code>heart</code></p><p>Go to your assigned position and ping near a clown if it shows you a <code>heart</code></p><p>If 3 clowns have a <code>heart</code> look at the clown with <code>confetti guns</code></p><p>If 3 clown have a <code>confetti guns</code> look at the clown with a <code>heart</code></p><h2 id="\u221E-hp-dice" tabindex="-1">\u221E HP <strong>Dice</strong> <a class="header-anchor" href="#\u221E-hp-dice" aria-hidden="true">#</a></h2><p>This mechanic occurs set timers and is not bind to HP, this can be skipped if other major mechanic will occur at the same time.</p><p>Clown will throw a card player with matching suit should eat/tank the card</p><p><strong>Timers: 9:00, 5:30, 1:30 left on the enrage timer</strong></p><h4 id="telegraph-clown-throws-a-dice-in-the-air" tabindex="-1">Telegraph : Clown throws a dice in the air. <a class="header-anchor" href="#telegraph-clown-throws-a-dice-in-the-air" aria-hidden="true">#</a></h4><h4 id="position-as-below-clown-at-around-second-ring-of-the-arena-in-a-line-in-order-black-black-red-red-suits" tabindex="-1">Position as below clown at around second ring of the arena in a line in order black,black,red,red suits <a class="header-anchor" href="#position-as-below-clown-at-around-second-ring-of-the-arena-in-a-line-in-order-black-black-red-red-suits" aria-hidden="true">#</a></h4><p>Card projectile will always spawn with the same colour back to back. If 1st card suit if red (diamond/heart) second card will be the other red suited card, and wise versa.</p><p>There is 2 variants of this mech</p><p>Variant 1. One player will have to guide cards to rooted players. Variant 2. One player will be rooted, other players should grab cards matching their suits that a flying towards rooted player.</p>',45),h=[i];function n(s,l,c,d,p,g){return t(),a("div",null,h)}var f=e(r,[["render",n]]);export{w as __pageData,f as default};
