import{_ as a,c as e,o as t,a as r}from"./app.722560e1.js";const g=JSON.parse('{"title":"Gate 2 Valtan","description":"","frontmatter":{"title":"Gate 2 Valtan"},"headers":[{"level":2,"title":"Preparation","slug":"preparation"},{"level":2,"title":"160 HP Armor Break","slug":"_160-hp-armor-break"},{"level":2,"title":"130 HP Star pizza wipe pattern","slug":"_130-hp-star-pizza-wipe-pattern"},{"level":2,"title":"110 HP Pillars","slug":"_110-hp-pillars"},{"level":2,"title":"85 \u2002HP Stage break","slug":"_85-hp-stage-break"},{"level":2,"title":"65 \u2002HP Counter","slug":"_65-hp-counter"},{"level":2,"title":"35 \u2002HP Stage Break","slug":"_35-hp-stage-break"},{"level":2,"title":"17 \u2002HP Ghost Transition","slug":"_17-hp-ghost-transition"},{"level":2,"title":"40 \u2002HP Ghost Phase","slug":"_40-hp-ghost-phase"}],"relativePath":"valtan/gate2.md"}'),i={name:"valtan/gate2.md"},h=r('<h1 id="gate-2-valtan-hard-mode" tabindex="-1">Gate 2 Valtan <code>--Hard Mode--</code> <a class="header-anchor" href="#gate-2-valtan-hard-mode" aria-hidden="true">#</a></h1><p>Table of content</p><nav class="table-of-contents"><ul><li><a href="#preparation">Preparation</a><ul></ul></li><li><a href="#_160-hp-armor-break">160 HP Armor Break</a></li><li><a href="#_130-hp-star-pizza-wipe-pattern">130 HP Star pizza wipe pattern</a></li><li><a href="#_110-hp-pillars">110 HP Pillars</a></li><li><a href="#_85-hp-stage-break">85 \u2002HP Stage break</a></li><li><a href="#_65-hp-counter">65 \u2002HP Counter</a></li><li><a href="#_35-hp-stage-break">35 \u2002HP Stage Break</a></li><li><a href="#_17-hp-ghost-transition">17 \u2002HP Ghost Transition</a></li><li><a href="#_40-hp-ghost-phase">40 \u2002HP Ghost Phase</a></li></ul></nav><h2 id="preparation" tabindex="-1">Preparation <a class="header-anchor" href="#preparation" aria-hidden="true">#</a></h2><h4 id="items" tabindex="-1">Items: <a class="header-anchor" href="#items" aria-hidden="true">#</a></h4><p>HP Potion</p><p>Time Stop Potion</p><p>1 Player Corrosive Bomb/ Rest use Destruction Bomb</p><h4 id="ester" tabindex="-1">Ester <a class="header-anchor" href="#ester" aria-hidden="true">#</a></h4><p>Balthorr</p><p>Thirain</p><h4 id="positions" tabindex="-1">Positions <a class="header-anchor" href="#positions" aria-hidden="true">#</a></h4><p>No positions needed</p><h4 id="notes" tabindex="-1">Notes <a class="header-anchor" href="#notes" aria-hidden="true">#</a></h4><p>DPS check are hard on item level</p><h2 id="_160-hp-armor-break" tabindex="-1">160 HP Armor Break <a class="header-anchor" href="#_160-hp-armor-break" aria-hidden="true">#</a></h2><p>Stay next to a pillar and wait for Valtan to charge it</p><p>Use corrosive bomb when he start charge</p><p>Use destruction bombs + skills when he gets stunned</p><h2 id="_130-hp-star-pizza-wipe-pattern" tabindex="-1">130 HP Star pizza wipe pattern <a class="header-anchor" href="#_130-hp-star-pizza-wipe-pattern" aria-hidden="true">#</a></h2><p>Use ester Balthorr to cheese this mechanic</p><h2 id="_110-hp-pillars" tabindex="-1">110 HP Pillars <a class="header-anchor" href="#_110-hp-pillars" aria-hidden="true">#</a></h2><p>Selected player must kite huge red pizza between 2 pillars and just away behind the pillar at the last moment when pizza stops moving</p><p>Rest of the players just hide behind the pillars</p><p>He will repeat pizza again, that one can be tanked</p><h2 id="_85-hp-stage-break" tabindex="-1">85 \u2002HP Stage break <a class="header-anchor" href="#_85-hp-stage-break" aria-hidden="true">#</a></h2><p>Stay near the middle when Valtan is at that HP, upon break run to the safe part of the map</p><p>Pillar mechanic will happen again, but with half of the arena</p><h2 id="_65-hp-counter" tabindex="-1">65 \u2002HP Counter <a class="header-anchor" href="#_65-hp-counter" aria-hidden="true">#</a></h2><p>Counter 3s after animation stops</p><h2 id="_35-hp-stage-break" tabindex="-1">35 \u2002HP Stage Break <a class="header-anchor" href="#_35-hp-stage-break" aria-hidden="true">#</a></h2><p>Pillar mechanic will happen again, but with only middle of the arena left</p><h2 id="_17-hp-ghost-transition" tabindex="-1">17 \u2002HP Ghost Transition <a class="header-anchor" href="#_17-hp-ghost-transition" aria-hidden="true">#</a></h2><p>Use ester Balthorr, to cheese this mechanic</p><h2 id="_40-hp-ghost-phase" tabindex="-1">40 \u2002HP Ghost Phase <a class="header-anchor" href="#_40-hp-ghost-phase" aria-hidden="true">#</a></h2><p>Valtan will have damage reduction buff, counter side cows to reduce</p><p>Use Thirain on on cooldown</p>',37),n=[h];function s(p,l,o,d,c,_){return t(),e("div",null,n)}var b=a(i,[["render",s]]);export{g as __pageData,b as default};
